import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useTask } from '../contexts/TaskContext';
import Sidebar from './layout/Sidebar';
import Header from './layout/Header';
import TaskForm from './tasks/TaskForm';
import KanbanBoard from './tasks/KanbanBoard';
import TaskList from './tasks/TaskList';

export default function Dashboard() {
  const [view, setView] = useState('kanban'); // 'kanban' or 'list'
  const [showTaskForm, setShowTaskForm] = useState(false);
  const { user, logout } = useAuth();
  const { tasks, loading, filter, setFilter } = useTask();

  const handleSearch = (searchTerm) => {
    setFilter(prev => ({ ...prev, search: searchTerm }));
  };

  const handleFilterChange = (filterType, value) => {
    setFilter(prev => ({ ...prev, [filterType]: value }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating Orbs */}
        <div className="absolute top-20 left-20 w-32 h-32 bg-purple-500/20 rounded-full blur-xl animate-float"></div>
        <div className="absolute top-40 right-32 w-24 h-24 bg-blue-500/20 rounded-full blur-xl animate-bounce-slow"></div>
        <div className="absolute bottom-32 left-1/4 w-40 h-40 bg-indigo-500/10 rounded-full blur-2xl animate-pulse"></div>

        {/* Grid Pattern */}
        <div className="absolute inset-0 bg-grid-pattern opacity-5"></div>

        {/* Gradient Overlay */}
        <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"></div>
      </div>

      <div className="relative z-10 flex min-h-screen">
        {/* Sidebar */}
        <div className="animate-slide-in-left">
          <Sidebar 
            view={view} 
            setView={setView}
            filter={filter}
            onFilterChange={handleFilterChange}
          />
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col animate-slide-in-right">
          {/* Header */}
          <div className="animate-slide-in-up">
            <Header 
              user={user}
              onSearch={handleSearch}
              onLogout={logout}
              onNewTask={() => setShowTaskForm(true)}
            />
          </div>

          {/* Content Area */}
          <main className="flex-1 p-6 relative">
            {/* Loading State */}
            {loading && (
              <div className="absolute inset-0 bg-black/20 backdrop-blur-sm flex items-center justify-center z-50 rounded-2xl">
                <div className="glass p-8 rounded-2xl text-center">
                  <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-500 mx-auto mb-4"></div>
                  <p className="text-white font-medium">Loading your tasks...</p>
                </div>
              </div>
            )}

            {/* Content */}
            <div className="animate-slide-in-up" style={{ animationDelay: '0.2s' }}>
              {view === 'kanban' ? (
                <KanbanBoard tasks={tasks} />
              ) : (
                <TaskList tasks={tasks} />
              )}
            </div>

            {/* Floating Action Button */}
            <button
              onClick={() => setShowTaskForm(true)}
              className="fixed bottom-8 right-8 w-16 h-16 bg-gradient-to-r from-purple-600 to-blue-600 text-white rounded-full shadow-2xl hover:shadow-purple-500/25 transform hover:scale-110 transition-all duration-300 z-40 btn-3d group"
            >
              <svg className="w-8 h-8 mx-auto transform group-hover:rotate-90 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>

              {/* Ripple Effect */}
              <div className="absolute inset-0 rounded-full bg-white/20 scale-0 group-hover:scale-150 transition-transform duration-500 opacity-0 group-hover:opacity-100"></div>
            </button>

            {/* Stats Cards */}
            <div className="fixed bottom-8 left-8 space-y-2 z-30">
              <div className="glass p-3 rounded-xl text-white text-sm hover-lift">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                  <span>{tasks.filter(t => t.status === 'done').length} Completed</span>
                </div>
              </div>
              <div className="glass p-3 rounded-xl text-white text-sm hover-lift">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-pulse"></div>
                  <span>{tasks.filter(t => t.status === 'doing').length} In Progress</span>
                </div>
              </div>
              <div className="glass p-3 rounded-xl text-white text-sm hover-lift">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-yellow-400 rounded-full animate-pulse"></div>
                  <span>{tasks.filter(t => t.status === 'todo').length} To Do</span>
                </div>
              </div>
            </div>
          </main>
        </div>
      </div>

      {/* Task Form Modal */}
      {showTaskForm && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 animate-fade-in">
          <div className="animate-slide-in-up">
            <TaskForm 
              onClose={() => setShowTaskForm(false)}
            />
          </div>
        </div>
      )}

      {/* Particles */}
      <div className="particles">
        {[...Array(15)].map((_, i) => (
          <div
            key={i}
            className="particle"
            style={{
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 8}s`,
              animationDuration: `${8 + Math.random() * 4}s`
            }}
          ></div>
        ))}
      </div>
    </div>
  );
}
