import React, { useState } from 'react';
import { useTask } from '../../contexts/TaskContext';
import TaskForm from './TaskForm';

export default function TaskCard({ task, isDragging = false }) {
  const [showEditForm, setShowEditForm] = useState(false);
  const [showActions, setShowActions] = useState(false);
  const { updateTask, deleteTask, aiSummarize, aiSuggestPriority, aiAutoTag } = useTask();

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800 border-red-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'todo': return 'bg-gray-100';
      case 'doing': return 'bg-blue-100';
      case 'done': return 'bg-green-100';
      default: return 'bg-gray-100';
    }
  };

  const handleStatusChange = async (newStatus) => {
    await updateTask(task._id, { status: newStatus });
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      await deleteTask(task._id);
    }
  };

  const formatDate = (date) => {
    if (!date) return null;
    return new Date(date).toLocaleDateString();
  };

  const isOverdue = task.dueDate && new Date(task.dueDate) < new Date() && task.status !== 'done';

  return (
    <>
      <div 
        className={`bg-white rounded-lg shadow-sm border border-gray-200 p-4 hover:shadow-md transition-shadow ${
          isDragging ? 'opacity-50' : ''
        } ${isOverdue ? 'border-red-300' : ''}`}
        onMouseEnter={() => setShowActions(true)}
        onMouseLeave={() => setShowActions(false)}
      >
        {/* Header */}
        <div className="flex items-start justify-between mb-2">
          <h3 className="font-medium text-gray-900 line-clamp-2">{task.title}</h3>

          {/* Actions */}
          <div className={`flex items-center space-x-1 transition-opacity ${
            showActions ? 'opacity-100' : 'opacity-0'
          }`}>
            <button
              onClick={() => setShowEditForm(true)}
              className="p-1 text-gray-400 hover:text-gray-600 rounded"
              title="Edit task"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
              </svg>
            </button>
            <button
              onClick={handleDelete}
              className="p-1 text-gray-400 hover:text-red-600 rounded"
              title="Delete task"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
              </svg>
            </button>
          </div>
        </div>

        {/* Description */}
        {(task.summary || task.description) && (
          <p className="text-sm text-gray-600 mb-3 line-clamp-3">
            {task.summary || task.description}
          </p>
        )}

        {/* Tags */}
        {task.tags && task.tags.length > 0 && (
          <div className="flex flex-wrap gap-1 mb-3">
            {task.tags.map((tag, index) => (
              <span
                key={index}
                className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-indigo-100 text-indigo-800"
              >
                {tag}
              </span>
            ))}
          </div>
        )}

        {/* Footer */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            {/* Priority */}
            <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getPriorityColor(task.priority)}`}>
              {task.priority}
            </span>

            {/* Due Date */}
            {task.dueDate && (
              <span className={`text-xs ${isOverdue ? 'text-red-600 font-medium' : 'text-gray-500'}`}>
                {formatDate(task.dueDate)}
              </span>
            )}
          </div>

          {/* AI Actions */}
          <div className="flex items-center space-x-1">
            <button
              onClick={() => aiSummarize(task._id)}
              className="p-1 text-gray-400 hover:text-indigo-600 rounded"
              title="AI Summarize"
            >
              ✂️
            </button>
            <button
              onClick={() => aiSuggestPriority(task._id)}
              className="p-1 text-gray-400 hover:text-indigo-600 rounded"
              title="Suggest Priority"
            >
              🧠
            </button>
            <button
              onClick={() => aiAutoTag(task._id)}
              className="p-1 text-gray-400 hover:text-indigo-600 rounded"
              title="Auto Tag"
            >
              #
            </button>
          </div>
        </div>

        {/* Status Change Buttons (for non-kanban view) */}
        {task.status !== 'done' && (
          <div className="mt-3 flex space-x-2">
            {task.status === 'todo' && (
              <button
                onClick={() => handleStatusChange('doing')}
                className="text-xs px-2 py-1 bg-blue-100 text-blue-800 rounded hover:bg-blue-200 transition-colors"
              >
                Start
              </button>
            )}
            {task.status === 'doing' && (
              <button
                onClick={() => handleStatusChange('done')}
                className="text-xs px-2 py-1 bg-green-100 text-green-800 rounded hover:bg-green-200 transition-colors"
              >
                Complete
              </button>
            )}
          </div>
        )}
      </div>

      {/* Edit Form Modal */}
      {showEditForm && (
        <TaskForm 
          task={task}
          onClose={() => setShowEditForm(false)}
        />
      )}
    </>
  );
}
